import React, { useState, useRef } from "react";
import { OverlayTrigger, Tooltip, Button, Form } from "react-bootstrap";

function Main(){

    const [ checkbox, setCheckBox ] = useState({
        alphabets: false,
        numbers: false,
        symbols: false
    });

    const [ showTooltip, setShowTooltip ] = useState(false);
    const tooltipRef = useRef();

    const showToolTip = () => {
        return <Tooltip id="tooltip" ref={tooltipRef}>this is tooltip text</Tooltip>
    };

    const copyPassword = () => {
        setTimeout(() => {
            let tooltipNode = document.querySelector('#tooltip')
            console.log(tooltipNode)
            if(tooltipNode) {
                tooltipNode.remove()
            }
        }, 1000)
    };

    return(
        <>
            <div className="container">
                <div className="card-wrapper d-flex align-items-center justify-content-center">
                    <div className="card">
                        <div className="card-header text-center">
                            Password Generator
                        </div>
                        <div className="card-body">

                        {/* <div className="tooltip bs-tooltip-top" role="tooltip" id="tooltip-top">
                            <div className="tooltip-arrow"></div>
                            <div className="tooltip-inner" style={{ color: 'red' }}>
                                Some tooltip text!
                            </div>
                        </div>
                        <Tooltip placement="top" className="in" id="tooltip-top">
                            Tooltip left
                        </Tooltip>
                        <button type="button" className="btn btn-secondary" data-toggle="tooltip-top" data-placement="top" title="Tooltip on top">
  Tooltip on top
</button> */}

                            <div className="password-field d-flex justify-content-between align-items-center rounded-2 p-1">
                                <span>gfdgfg$545</span>
                               
                                
                                <OverlayTrigger
                                    trigger="click"
                                    defaultShow={false}
                                    placement="top"
                                    // delayHide="150"
                                    overlay={showToolTip()}
                                >
                                    <Button variant="secondary" type="button" className="btn-outline-secondary btn-sm" style={{ color: '#fff' }} onClick={copyPassword}>Copy</Button>
                                </OverlayTrigger>
                            </div>
                            <Form> 
                                <Form.Group className="form-group d-grid">
                                    <Button variant="info" type="submit">Generate Password</Button>
                                </Form.Group>
                            </Form>
                        </div>
                    </div>
                </div>
            </div>
        </>
    )
}

export default Main;
